namespace SwitchVsIF
{
    internal class Program
    {
        /// <summary>
        ///     The main purpose of the project is to examine the IL code generated by <see langword="if" /> and
        ///     <see langword="switch" />.
        /// </summary>
        /// <param name="args"></param>
        private static void Main(string[] args)
        {
        }

        private static bool Switch_IsZero(int num)
        {
            switch (num)
            {
                case 0:
                    return true;
                default:
                    return false;
            }
        }

        private static bool If_IsZero(int num)
        {
            if (num == 0) return true;
            return false;
        }
    }
}